#!/usr/bin/env node

var start = Date.now();

var dcgt = require('../');
var program = require('commander'),
	path = require('path'),
	vdf = require('vdf-extra'),
	fs = require('fs-extra'),
	Promise = require('bluebird');

program
	.version('1.0.0')
	.option('-d, --dir, --directory, --in, --input [ip]', 'Path to localization directory')
	.option('-o, --out, --output [op]', "Path to 'game' directory")
	.parse(process.argv);

var cwd = process.cwd();

let gameDir = path.join(cwd, program.op || 'game');
var localeDir = path.join(cwd, program.ip || 'localization');
dcgt.ParseLocalesFromDirectory(localeDir).then(tokensByLanguages => {
	return Promise.all(Object.keys(tokensByLanguages).map(language => {
		let lang = {Language: language, Tokens: tokensByLanguages[language]};
		let minified = '\ufeff' + vdf.stringify({lang}, 0);
		return Promise.map([path.join(gameDir, 'resource/addon_' + language + '.txt'), path.join(gameDir, 'panorama/localization/addon_' + language + '.txt')], path => {
			return fs.ensureFile(path).then(() => fs.writeFile(path, minified, 'ucs2'));
		});
	}));
}).then(() => {
	console.log('Success! (' + (Date.now() - start) + 'ms)');
}).catch(console.error);
